using System;\nusing System.Security.Cryptography;\nusing System.Security.Cryptography.X509Certificates;\n\npublic class X509Example\n{\n    public static void CreateSelfSignedCertificate()\n    {\n        // Create a new RSA key pair\n        RSA rsa = RSA.Create(2048);\n\n        // Create a new certificate request\n        CertificateRequest req = new CertificateRequest(\n            "CN=Example Certificate",\n            rsa,\n            HashAlgorithmName.SHA256,\n            RSASignaturePadding.Pkcs1\n        );\n\n        // Create a self-signed certificate\n        X509Certificate2 cert = req.CreateSelfSigned(DateTime.UtcNow, DateTime.UtcNow.AddYears(1));\n\n        // Save the certificate to a file\n        File.WriteAllBytes("example.cer", cert.Export(X509ContentType.Cert));\n    }\n}