using System.Web.Http;\n\nnamespace MyApi\n{\n    public class MyController : ApiController\n    {\n        [HttpGet]\n        public string GetMyData()\n        {\n            // Return XML data\n            return MyData.ToXml();\n        }\n\n        [HttpPost]\n        public void CreateMyData(MyData data)\n        {\n            // Process XML data\n            MyData.FromXml(data.Xml);\n        }\n    }\n}