using System;\nusing System.IO;\nusing System.Text;\n\npublic class MemoryStreamToString\n{\n    public static string ReadStringFromMemoryStream(MemoryStream ms)\n    {\n        // Reset the position of the MemoryStream to the beginning\n        ms.Position = 0;\n\n        // Create a StreamReader to read the MemoryStream\n        using (StreamReader reader = new StreamReader(ms, Encoding.UTF8))\n        {\n            // Read the MemoryStream and return the string\n            return reader.ReadToEnd();\n        }\n    }\n}