using System;\nusing System.IO;\nusing System.Security.Cryptography;\n\nclass DPAPIExample\n{\n    static void Main(string[] args)\n    {\n        // Set the file path and data\n        string filePath = "path/to/your/file";\n        byte[] data = File.ReadAllBytes(filePath);\n\n        // Encrypt the data using DPAPI\n        byte[] encryptedData = ProtectedData.Protect(data, null, DataProtectionScope.CurrentUser);\n\n        // Decrypt the data using DPAPI\n        byte[] decryptedData = ProtectedData.Unprotect(encryptedData, null, DataProtectionScope.CurrentUser);\n\n        // Save the decrypted data to a file\n        File.WriteAllBytes(filePath, decryptedData);\n    }\n}