using System;\nusing System.Collections.Generic;\n\npublic class RateLimiter\n{\n    private static readonly Dictionary<string, int> _requestCounts = new Dictionary<string, int>();\n\n    public static bool IsAllowed(string ipAddress)\n    {\n        if (_requestCounts.ContainsKey(ipAddress))\n        {\n            int count = _requestCounts[ipAddress];\n            if (count > 10) // adjust threshold as needed\n            {\n                return false;\n            }\n            _requestCounts[ipAddress] = count + 1;\n        }\n        else\n        {\n            _requestCounts.Add(ipAddress, 1);\n        }\n        return true;\n    }\n}