using System;\nusing System.Xml.Linq;\n\npublic class XmlParser\n{\n    public static void ParseXml(string xml)\n    {\n        XDocument doc = XDocument.Parse(xml);\n\n        var nodes = doc.Descendants(XName.Get("cdr", "http://dummy.com"));\n\n        foreach (XElement node in nodes)\n        {\n            string callId = node.Element(XName.Get("call_id", "http://dummy.com")).Value;\n            string cdrId = node.Element(XName.Get("cdr_id", "http://dummy.com")).Value;\n            string status = node.Element(XName.Get("status", "http://dummy.com")).Value;\n\n            Console.WriteLine($"Call Id: {callId}, CDR Id: {cdrId}, Status: {status}");\n        }\n    }\n}