public class CustomConstraint : Constraint\n{\n    private readonly string _expectedValue;\n\n    public CustomConstraint(string expectedValue)\n    {\n        _expectedValue = expectedValue;\n    }\n\n    public override void WriteDescriptionTo(MessageWriter writer)\n    {\n        writer.Write("Expected value to be \"{0}\"", _expectedValue);\n    }\n\n    public override void WriteMessageTo(MessageWriter writer)\n    {\n        base.WriteMessageTo(writer); // Calls WriteDescriptionTo and appends failure info\n    }\n\n    public override void WriteActualValueTo(MessageWriter writer)\n    {\n        writer.Write("Actual value was \"{0}\"", actualValue); // assume 'actualValue' is a field\n    }\n}