using System;\nusing System.Xml;\n\npublic class XmlParser\n{\n    public static void ParseXml(string xml)\n    {\n        XmlDocument doc = new XmlDocument();\n        doc.LoadXml(xml);\n\n        XmlNamespaceManager nsmgr = new XmlNamespaceManager(doc.NameTable);\n        nsmgr.AddNamespace("cdr", "http://dummy.com"); // dummy namespace\n\n        XmlNodeList nodes = doc.SelectNodes("//cdr:cdr", nsmgr);\n\n        foreach (XmlNode node in nodes)\n        {\n            string callId = node.SelectSingleNode("cdr:call_id", nsmgr).InnerText;\n            string cdrId = node.SelectSingleNode("cdr:cdr_id", nsmgr).InnerText;\n            string status = node.SelectSingleNode("cdr:status", nsmgr).InnerText;\n\n            Console.WriteLine($"Call Id: {callId}, CDR Id: {cdrId}, Status: {status}");\n        }\n    }\n}