using System.Diagnostics;\n\nstring serviceName = "YourServiceName";\n\n// Get the process ID of the service\nProcess process = new Process();\nprocess.StartInfo.FileName = "sc";\nprocess.StartInfo.Arguments = $"query {serviceName}";\nprocess.StartInfo.UseShellExecute = false;\nprocess.StartInfo.RedirectStandardOutput = true;\nprocess.Start();\n\nstring output = process.StandardOutput.ReadToEnd();\nprocess.WaitForExit();\n\n// Extract the process ID from the output\nstring[] lines = output.Split('\n');\nforeach (string line in lines)\n{\n    if (line.Contains("PROCESS_ID"))\n    {\n        string[] parts = line.Split(':');\n        string processId = parts[1].Trim();\n        // Kill the process\n        Process KillProcess = Process.GetProcessById(int.Parse(processId));\n        KillProcess.Kill();\n        break;\n    }\n}