SqlQuery sq = new Select(\n    new[] {\n        Tables.TableOne + "." + TableOne.Columns.MemberId + " AS MemberId",\n        Tables.TableTwo + "." + TableTwo.Columns.Name + " AS MemberName",\n        Tables.TableOne + "." + TableOne.Columns.ExpiryOn + " AS MembershipExpiresOn",\n        Tables.TableFour + "." + TableFour.Columns.Name + " AS Country"\n    }\n)\n.From(DAL.Tables.TableOne)\n.InnerJoin(Tables.TableTwo)\n.InnerJoin(Tables.TableThree)\n.InnerJoin(Tables.TableFour, TableFour.Columns.CountryCode, Tables.TableThree, TableThree.Columns.CountryOfBirth);\n\nstring paramName = "@name";\nstring tableName = Tables.TableTwo;\nstring columnName = TableTwo.Columns.Name;\n\nsq.Where(columnName, tableName).Like(paramName);\nsq.AddParameter(paramName, DbType.String, "A%");