using System;\nusing System.Linq;\n\npublic static bool IsSubset(byte[] largeArray, byte[] subArray)\n{\n    int largeArrayLength = largeArray.Length;\n    int subArrayLength = subArray.Length;\n\n    for (int i = 0; i <= largeArrayLength - subArrayLength; i++)\n    {\n        bool match = true;\n\n        for (int j = 0; j < subArrayLength; j++)\n        {\n            if (largeArray[i + j] != subArray[j])\n            {\n                match = false;\n                break;\n            }\n        }\n\n        if (match)\n        {\n            return true;\n        }\n    }\n\n    return false;\n}