using Microsoft.Win32;\n\n// Set association for ".smc" files\nstring extension = ".smc";\nstring associatedApp = "YourApp.exe";\n\n// Get the registry key for the extension\nRegistryKey key = Registry.ClassesRoot.CreateSubKey(extension);\n\n// Set the default value to the associated app\nkey.SetValue("", associatedApp);\n\n// Create a new key for the associated app\nRegistryKey appKey = Registry.ClassesRoot.CreateSubKey(associatedApp);\n\n// Set the "open" verb to launch the associated app\nappKey.CreateSubKey("shell\\open\\command").SetValue("", $"\"{associatedApp}\" \"%1\"");