using System;\nusing System.Collections.Generic;\nusing System.IO;\nusing System.Linq;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Load carrier prefix codes from CSV file\n        string[] prefixCodes = File.ReadAllLines("carrier_prefix_codes.csv");\n\n        // Create a HashSet for fast lookups\n        var prefixCodeSet = new HashSet<string>(prefixCodes);\n\n        // Load dialed numbers and minutes from CSV file\n        string[] dialedNumbers = File.ReadAllLines("dialed_numbers.csv");\n\n        // Create a dictionary to store subtotals for each carrier\n        var subtotals = new Dictionary<string, int>();\n\n        // Iterate through dialed numbers and minutes\n        foreach (string line in dialedNumbers)\n        {\n            string[] parts = line.Split(',');\n            string dialedNumber = parts[0];\n            int minutes = int.Parse(parts[1]);\n\n            // Iterate through prefix codes\n            foreach (string prefixCode in prefixCodeSet)\n            {\n                if (dialedNumber.StartsWith(prefixCode))\n                {\n                    // Match found, add minutes to subtotal\n                    if (!subtotals.TryGetValue(prefixCode, out int subtotal))\n                    {\n                        subtotal = 0;\n                    }\n                    subtotal += minutes;\n                    subtotals[prefixCode] = subtotal;\n                }\n            }\n        }\n\n        // Print subtotals for each carrier\n        foreach (var pair in subtotals)\n        {\n            Console.WriteLine($"Carrier {pair.Key}: {pair.Value} minutes");\n        }\n    }\n}