using System.Net.Http;\n\npublic class PhpProxy : IHttpHandler\n{\n    public void ProcessRequest(HttpContext context)\n    {\n        var phpUrl = "http://your-php-server.com/your-php-page.php";\n        var proxy = new HttpClient();\n        var response = proxy.GetAsync(phpUrl).Result;\n\n        context.Response.StatusCode = (int)response.StatusCode;\n        context.Response.StatusDescription = response.ReasonPhrase;\n        context.Response.Write(response.Content.ReadAsStringAsync().Result);\n    }\n}