using (SqlConnection conn = new SqlConnection(connectionString))\n{\n    using (SqlCommand com = new SqlCommand("GetMultipleResults", conn))\n    {\n        com.CommandType = CommandType.StoredProcedure;\n        com.Parameters.AddWithValue("@Id", id);\n\n        conn.Open();\n        SqlDataReader reader = com.ExecuteReader();\n\n        // Read the first result set (TableA)\n        while (reader.Read())\n        {\n            // Process the data from TableA\n        }\n\n        // Move to the next result set (TableB)\n        reader.NextResult();\n\n        // Read the second result set (TableB)\n        while (reader.Read())\n        {\n            // Process the data from TableB\n        }\n\n        reader.Close();\n    }\n}