using System;\nusing System.Data;\nusing Oracle.ManagedDataAccess.Client;\n\nnamespace OracleDatabaseAccessExample\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            // Set up the database connection string\n            string connectionString = "Data Source=(DESCRIPTION=(ADDRESS=(PROTOCOL=TCP)(HOST=your-oracle-database-host)(PORT=your-oracle-database-port))(CONNECT_DATA=(SERVICE_NAME=your-oracle-database-service-name)));User ID=your-username;Password=your-password";\n\n            // Create an Oracle connection and command\n            OracleConnection connection = new OracleConnection(connectionString);\n            OracleCommand command = new OracleCommand("SELECT * FROM employees", connection);\n\n            // Open the connection and execute the command\n            connection.Open();\n            OracleDataReader reader = command.ExecuteReader();\n\n            // Process the employee data\n            while (reader.Read())\n            {\n                Console.WriteLine(reader["name"].ToString());\n            }\n\n            // Close the connection\n            connection.Close();\n        }\n    }\n}