using System;\nusing System.Web;\nusing System.Web.Security;\n\npublic class ElmahRssAuthModule : IHttpModule\n{\n    public void Init(HttpApplication application)\n    {\n        application.AuthenticateRequest += new EventHandler(OnAuthenticateRequest);\n    }\n\n    private void OnAuthenticateRequest(object sender, EventArgs e)\n    {\n        var request = ((HttpApplication)sender).Request;\n        var response = ((HttpApplication)sender).Response;\n\n        if (request.Url.PathAndQuery.StartsWith("/elmah.axd/rss", StringComparison.OrdinalIgnoreCase))\n        {\n            var username = "your_username";\n            var password = "your_password";\n\n            var credentials = new NetworkCredential(username, password);\n            var principal = new GenericPrincipal(new GenericIdentity(username), new[] { "ELMAH_RSS_ACCESS" });\n\n            var authCookie = FormsAuthentication.GetAuthCookie(username, true);\n            response.Cookies.Add(authCookie);\n\n            Thread.CurrentThread.CurrentCulture = new CultureInfo("en-US");\n            Thread.CurrentThread.CurrentUICulture = new CultureInfo("en-US");\n\n            HttpContext.Current.User = principal;\n        }\n    }\n\n    public void Dispose()\n    {\n        // Nothing to dispose\n    }\n}