using System;\nusing System.ComponentModel;\nusing System.Windows.Forms;\nusing System.Windows.Forms.Design;\n\n[Designer(typeof(MyControlDesigner))]\npublic class MyUserControl : UserControl\n{\n    public string StringProperty1 { get; set; }\n    public string StringProperty2 { get; set; }\n    public int IntegerProperty1 { get; set; }\n    public int IntegerProperty2 { get; set; }\n\n    // ...\n}\n\npublic class MyControlDesigner : ControlDesigner\n{\n    private MyUserControl control;\n\n    public override void Initialize(IComponent component)\n    {\n        base.Initialize(component);\n        control = (MyUserControl)component;\n    }\n\n    protected override string GetErrorDesignTimeHtml(Exception e)\n    {\n        return base.GetErrorDesignTimeHtml(e);\n    }\n\n    public override string GetDesignTimeHtml()\n    {\n        if (control != null)\n        {\n            string html = $"<div>{control.StringProperty1}</div>";\n            html += $"<div>{control.StringProperty2}</div>";\n            html += $"<div>{control.IntegerProperty1}</div>";\n            html += $"<div>{control.IntegerProperty2}</div>";\n            return html;\n        }\n        return base.GetDesignTimeHtml();\n    }\n}