using System.IO;\nusing System.Web;\n\nnamespace ClickOnceHandler\n{\n    public class ClickOnceHandler : IHttpHandler\n    {\n        public void ProcessRequest(HttpContext context)\n        {\n            string filePath = @"\\networkshare\ApplicationDirectory\ApplicationName.application";\n            string mimeType = "application/x-ms-application";\n\n            context.Response.ContentType = mimeType;\n            context.Response.WriteFile(filePath);\n        }\n\n        public bool IsReusable { get { return true; } }\n    }\n}