var groupedData = yourDataTable.AsEnumerable()\n    .GroupBy(row => row.Field<string>("AddressA"))\n    .Select(g => new\n    {\n        AddressA = g.Key,\n        Bytes = g.Sum(row => row.Field<uint>("Bytes"))\n    });\n\nvar resultDataTable = new DataTable();\nresultDataTable.Columns.Add("AddressA", typeof(string));\nresultDataTable.Columns.Add("Bytes", typeof(uint));\n\nforeach (var item in groupedData)\n{\n    resultDataTable.Rows.Add(item.AddressA, item.Bytes);\n}