using System.Text.RegularExpressions;\n\npublic static string ToFriendlyUrl(string input)\n{\n    input = input.ToLower();\n    input = Regex.Replace(input, @"[^\w\s-]", ""); // remove non-word characters\n    input = Regex.Replace(input, @"\s+", "-"); // replace multiple spaces with a single dash\n    input = input.Trim('-', '_'); // trim dashes and underscores\n    return input;\n}