using iTextSharp.text.pdf;\nusing DocumentFormat.OpenXml.Packaging;\n\n// Load the Word document using Open XML SDK\nusing (WordprocessingDocument wordDoc = WordprocessingDocument.Open("input.docx", true))\n{\n    // Convert the Word document to PDF using iTextSharp\n    PdfWriter writer = PdfWriter.GetInstance(wordDoc.MainDocumentPart.Document, new FileStream("output.pdf", FileMode.Create));\n    writer.Close();\n}