public class CustomAuthorizationManager : ServiceAuthorizationManager\n{\n    protected override bool CheckAccessCore(OperationContext operationContext)\n    {\n        // Get the authentication headers\n        var headers = operationContext.RequestMessage.Headers;\n        var authHeader = headers.Where(h => h.Name == "Authorization").FirstOrDefault();\n\n        if (authHeader != null)\n        {\n            // Extract the username and password from the header\n            var credentials = authHeader.Value.Trim().Split(':');\n            if (credentials.Length == 2)\n            {\n                var username = credentials[0].Trim();\n                var password = credentials[1].Trim();\n\n                // Validate the username and password here\n                // Replace this with your custom validation logic\n                if (username == "test" && password == "password")\n                {\n                    return true;\n                }\n            }\n        }\n\n        return false;\n    }\n}