using System;\nusing Tamir.SharpSsh;\n\nclass SftpExample\n{\n    static void Main()\n    {\n        try\n        {\n            // Create a new SFTP client\n            Sftp client = new Sftp("sftp.example.com", "username", "password");\n\n            // Connect to the SFTP server\n            client.Connect();\n\n            // Upload a file\n            client.Put("localfile.txt", "remotefile.txt");\n\n            // Download a file\n            client.Get("remotefile.txt", "localfile.txt");\n\n            // Close the connection\n            client.Close();\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("An error occurred: " + ex.Message);\n        }\n    }\n}