using NAudio.Codecs;\nusing NAudio.Wave;\n\npublic class G729ToWavConverter\n{\n    public void ConvertG729ToWav(string g729FilePath, string wavFilePath)\n    {\n        // Create a G729 decoder\n        using (var g729Decoder = new G729Decoder())\n        {\n            // Open the G729 file\n            using (var g729File = new FileStream(g729FilePath, FileMode.Open))\n            {\n                // Create a WAV writer\n                using (var wavWriter = new WaveFileWriter(wavFilePath, new WaveFormat(8000, 16, 1)))\n                {\n                    // Decode G729 and write to WAV\n                    byte[] buffer = new byte[1024];\n                    int bytesRead;\n                    while ((bytesRead = g729File.Read(buffer, 0, buffer.Length)) > 0)\n                    {\n                        var decodedBuffer = g729Decoder.Decode(buffer, bytesRead);\n                        wavWriter.Write(decodedBuffer, 0, decodedBuffer.Length);\n                    }\n                }\n            }\n        }\n    }\n}