public static string[] SortIntoColumns(string[] array, int columns)\n{\n    if (array.Length <= columns)\n    {\n        return array;\n    }\n\n    string[] result = new string[(array.Length + columns - 1) / columns];\n    for (int i = 0; i < result.Length; i++)\n    {\n        StringBuilder sb = new StringBuilder();\n        for (int j = 0; j < columns; j++)\n        {\n            int index = i + j * result.Length;\n            if (index < array.Length)\n            {\n                sb.Append(array[index]);\n            }\n            else\n            {\n                sb.Append(" ");\n            }\n        }\n        result[i] = sb.ToString();\n    }\n    return result;\n}