// Example of using the Authorize attribute\n[Authorize]\npublic class MyController : Controller\n{\n    // ...\n}\n\n// Example of using ModelState for input validation\npublic class MyController : Controller\n{\n    [HttpPost]\n    public ActionResult MyAction(MyModel model)\n    {\n        if (!ModelState.IsValid)\n        {\n            return View(model);\n        }\n        // ...\n    }\n}\n\n// Example of using Html.Encode to prevent JavaScript injection attacks\npublic class MyController : Controller\n{\n    public ActionResult MyAction()\n    {\n        string userInput = "Hello, <script>alert('XSS')</script>!";\n        return View((object)Html.Encode(userInput));\n    }\n}