public static long CalculateCommonMultiplier(decimal[] numbers)\n{\n    long[] denominators = new long[numbers.Length];\n    for (int i = 0; i < numbers.Length; i++)\n    {\n        denominators[i] = GetDenominator(numbers[i]);\n    }\n    return LCM(denominators);\n}\n\npublic static long GetDenominator(decimal number)\n{\n    string str = number.ToString("0.000000000", CultureInfo.InvariantCulture);\n    int decimalIndex = str.IndexOf('.');\n    if (decimalIndex == -1)\n    {\n        return 1;\n    }\n    else\n    {\n        return (long)Math.Pow(10, str.Length - decimalIndex - 1);\n    }\n}