using System;\nusing System.Web.UI;\nusing System.Web.UI.WebControls;\nusing Microsoft.SharePoint;\nusing Microsoft.SharePoint.WebControls;\n\nnamespace CustomListSettings\n{\n    public partial class CategoriesListSettings : ListSettings\n    {\n        protected DropDownList ddlCategories;\n\n        protected override void OnInit(EventArgs e)\n        {\n            base.OnInit(e);\n\n            // Initialize the categories dropdown\n            ddlCategories = new DropDownList();\n            ddlCategories.Items.Add(new ListItem("Category 1"));\n            ddlCategories.Items.Add(new ListItem("Category 2"));\n            // ...\n\n            // Add the dropdown to the page\n            this.Controls.Add(ddlCategories);\n        }\n\n        protected override void OnLoad(EventArgs e)\n        {\n            base.OnLoad(e);\n\n            // Get the current list\n            SPList list = SPContext.Current.List;\n\n            // Check if the list is of your custom type\n            if (list.Fields.Contains("MyCustomField"))\n            {\n                // Show the categories dropdown\n                ddlCategories.Visible = true;\n            }\n            else\n            {\n                // Hide the categories dropdown\n                ddlCategories.Visible = false;\n            }\n        }\n    }\n}