public static long LCM(long[] numbers)\n{\n    long lcm = numbers[0];\n    for (int i = 1; i < numbers.Length; i++)\n    {\n        lcm = LCM(lcm, numbers[i]);\n    }\n    return lcm;\n}\n\npublic static long LCM(long a, long b)\n{\n    return (a / GCD(a, b)) * b;\n}\n\npublic static long GCD(long a, long b)\n{\n    while (b != 0)\n    {\n        long temp = b;\n        b = a % b;\n        a = temp;\n    }\n    return a;\n}