private BackgroundWorker _mapLoader;\n\n// Initialize the background worker\npublic YourForm()\n{\n    InitializeComponent();\n    _mapLoader = new BackgroundWorker();\n    _mapLoader.DoWork += LoadMapWorker_DoWork;\n    _mapLoader.RunWorkerCompleted += LoadMapWorker_RunWorkerCompleted;\n}\n\nprivate void LoadMapButton_Click(object sender, EventArgs e)\n{\n    _mapLoader.RunWorkerAsync();\n}\n\nprivate void LoadMapWorker_DoWork(object sender, DoWorkEventArgs e)\n{\n    // Load the map on a separate thread\n    axMappointControl1.NewMap(MapPoint.GeoMapRegion.geoMapNorthAmerica);\n}\n\nprivate void LoadMapWorker_RunWorkerCompleted(object sender, RunWorkerCompletedEventArgs e)\n{\n    // Update the UI when the map is loaded\n    // You can display a loading dialog or update the UI here\n    MessageBox.Show("Map loaded!");\n}