// Example using iTextSharp\nusing iTextSharp.text.pdf;\n\n// Open the PDF file\nusing (var pdfReader = new PdfReader("input.pdf"))\n{\n    // Get the first page\n    var page = pdfReader.GetPageN(1);\n\n    // Extract the vector graphics\n    var graphics = page.GetGraphics();\n\n    // Save the graphics to a new file\n    using (var writer = new PdfWriter("output.pdf"))\n    {\n        writer.AddPage(graphics);\n    }\n}