protected void Page_Load(object sender, EventArgs e)\n{\n    // Dynamically render the controls\n    var dynamicControl = new TextBox();\n    dynamicControl.ID = "dynamicControl";\n\n    var unitDropdown = new DropDownList();\n    unitDropdown.ID = "unit";\n    unitDropdown.Items.Add(new ListItem("Hands", "hands"));\n    unitDropdown.Items.Add(new ListItem("CM", "cm"));\n\n    // Add the custom validation attribute to the dynamic control\n    dynamicControl.Attributes.Add(UnitValidationAttribute.Unit, "hands");\n    dynamicControl.Attributes.Add(UnitValidationAttribute.Min, "15");\n    dynamicControl.Attributes.Add(UnitValidationAttribute.Max, "40");\n\n    // Add the dynamic controls to the page\n    Page.Controls.Add(dynamicControl);\n    Page.Controls.Add(unitDropdown);\n}