protected void UploadButton_Click(object sender, EventArgs e)\n{\n    if (fileUpload.HasFile)\n    {\n        string fileExtension = System.IO.Path.GetExtension(fileUpload.FileName).ToLower();\n        string[] allowedExtensions = { ".xls", ".xlsx" };\n\n        if (allowedExtensions.Contains(fileExtension))\n        {\n            // File extension is valid, proceed with the upload\n            fileUpload.SaveAs(Server.MapPath("~/Uploads/") + fileUpload.FileName);\n            // Success message or actions\n        }\n        else\n        {\n            // File extension is not valid, show error message\n            ErrorMessage.Text = "Only .xls and .xlsx file types are allowed.";\n        }\n    }\n    else\n    {\n        // No file was selected, show error message\n        ErrorMessage.Text = "Please select a file to upload.";\n    }\n}