using (SqlConnection connection = new SqlConnection(connectionString))\n{\n    connection.Open();\n\n    using (SqlCommand command = new SqlCommand("SELECT YourColumn FROM YourTable", connection))\n    {\n        using (SqlDataReader reader = command.ExecuteReader())\n        {\n            while (reader.Read())\n            {\n                using (XmlTextReader xmlReader = reader.GetTextReader(0))\n                {\n                    DataContractSerializer deserializer = new DataContractSerializer(typeToDeserialize);\n                    return deserializer.ReadObject(xmlReader);\n                }\n            }\n        }\n    }\n}