using System.Text.RegularExpressions;\n\nstring pattern = @"<a\s+[^>]*href\s*=\s*[""'](.*?)[""'][^>]*>(.*?)</a>";\n\n// Use RegexOptions.IgnoreCase for case insensitivity\nRegex regex = new Regex(pattern, RegexOptions.IgnoreCase | RegexOptions.Singleline);\n\nstring html = "Your HTML string here";\n\nMatchCollection matches = regex.Matches(html);\n\nforeach (Match match in matches)\n{\n    string hyperlink = match.Groups[1].Value;\n    string description = match.Groups[2].Value;\n\n    // Remove any HTML tags from the description\n    description = Regex.Replace(description, @"<.*?>", "");\n\n    Console.WriteLine($"Hyperlink: {hyperlink}, Description: {description}");\n}