string sql = "SELECT [UserId] FROM [UserProfiles] WHERE NOT [UserId] = @CurrentUserId";\n\nstring strCon = System.Web.Configuration.WebConfigurationManager.ConnectionStrings["SocialSiteConnectionString"].ConnectionString;\n\nusing (SqlConnection conn = new SqlConnection(strCon))\n{\n    using (SqlCommand comm = new SqlCommand(sql, conn))\n    {\n        comm.Parameters.AddWithValue("@CurrentUserId", "CurrentUserId"); // parameterize the query to prevent SQL injection\n\n        conn.Open();\n\n        using (SqlDataReader reader = comm.ExecuteReader())\n        {\n            while (reader.Read())\n            {\n                string userId = reader["UserId"].ToString(); // access the UserId column\n                // do something with the userId\n            }\n        }\n    }\n}