// Assuming you have a DataContext named 'db'\n\n// Define the association in the DataContext\npublic partial class MyDataContext : DataContext\n{\n    // ...\n\n    private static MappingSource mappingSource = new AttributeMappingSource();\n\n    public MyDataContext(string connection) : base(connection, mappingSource) { }\n\n    [Table(Name = "dbo.MyTable")]\n    public class MyTable\n    {\n        [Column(IsPrimaryKey = true, IsDbGenerated = true)]\n        public int Id { get; set; }\n\n        // Other properties\n    }\n\n    [Table(Name = "dbo.MyView")]\n    public class MyView\n    {\n        [Column(IsPrimaryKey = true)]\n        public int Id { get; set; }\n\n        // Other properties\n    }\n\n    // Define the 1-to-1 association\n    [Association(Name = "FK_MyTable_MyView", Storage = "myView", ThisKey = "Id", OtherKey = "Id", IsForeignKey = true)]\n    public MyView MyViewProperty\n    {\n        get { return this.myView.Entity; }\n        set { this.myView.Entity = value; }\n    }\n\n    private EntityRef<MyView> myView;\n}