using System;\nusing System.Web;\n\npublic class RestHandler : IHttpHandler\n{\n    public void ProcessRequest(HttpContext context)\n    {\n        string method = context.Request.HttpMethod;\n\n        if (method == "GET")\n        {\n            // Handle GET request\n            string id = context.Request.QueryString["id"];\n            if (!string.IsNullOrEmpty(id))\n            {\n                // Return the requested resource\n                context.Response.Write("You requested resource with id: " + id);\n            }\n            else\n            {\n                // Return a list of available resources\n                context.Response.Write("Available resources: ...");\n            }\n        }\n        else if (method == "POST")\n        {\n            // Handle POST request\n            string inputData = context.Request.Form["data"];\n            // Process the input data\n            context.Response.Write("Data processed successfully");\n        }\n        else\n        {\n            context.Response.StatusCode = 405; // Method Not Allowed\n        }\n    }\n\n    public bool IsReusable\n    {\n        get { return true; }\n    }\n}