using FAXCOMEXLib;\n\nclass FaxSender\n{\n    public void SendFax(string faxNumber, string faxData)\n    {\n        FaxServer faxServer = new FaxServer();\n        faxServer.GetFaxServer(); // retrieve the fax server instance\n\n        FaxDocument faxDocument = new FaxDocument();\n        faxDocument.FaxNumber = faxNumber;\n        faxDocument.DocumentName = "Fax Document";\n\n        faxDocument.Submit(faxServer); // submit the fax job\n    }\n}