// Example of using regasm.exe to register the assembly\nregasm MyAssembly.dll /codebase\n\n// Example of using a callback interface instead of an event\n[ComVisible(true)]\npublic interface ICallback\n{\n    void ItemSelected(int someid1, int someid2);\n}\n\n[ComVisible(true)]\npublic class MyControl : UserControl\n{\n    private ICallback _callback;\n\n    public MyControl()\n    {\n        _callback = null;\n    }\n\n    public void SetCallback(ICallback callback)\n    {\n        _callback = callback;\n    }\n\n    void OnItemSelected(int someid1, int someid2)\n    {\n        if (_callback != null)\n        {\n            _callback.ItemSelected(someid1, someid2);\n        }\n    }\n}