using System;\nusing System.IO;\nusing System.Data.SqlClient;\n\npublic partial class DisplayImage : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        string connectionString = "Your SQL Server connection string";\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n\n            string query = "SELECT ImageData, ContentType FROM Images WHERE ImageId = @ImageId";\n            SqlCommand command = new SqlCommand(query, connection);\n            command.Parameters.AddWithValue("@ImageId", Request.QueryString["ImageId"]);\n\n            SqlDataReader reader = command.ExecuteReader();\n            if (reader.Read())\n            {\n                byte[] imageData = (byte[])reader["ImageData"];\n                string contentType = reader["ContentType"].ToString();\n\n                Response.ContentType = contentType;\n                Response.OutputStream.Write(imageData, 0, imageData.Length);\n                Response.End();\n            }\n        }\n    }\n}