private bool RemoteCertificateValidation(object sender, X509Certificate certificate, X509Chain chain, SslPolicyErrors sslPolicyErrors)\n{\n    // Load your own list of trusted certificates\n    var trustedCertificates = new X509Certificate2Collection();\n    // Add your trusted certificates to the collection\n    trustedCertificates.Add(new X509Certificate2("path/to/trusted/cert1.cer"));\n    trustedCertificates.Add(new X509Certificate2("path/to/trusted/cert2.cer"));\n\n    // Check if the client's certificate is in the list of trusted certificates\n    foreach (var trustedCertificate in trustedCertificates)\n    {\n        if (trustedCertificate.SubjectName.Name == certificate.SubjectName.Name)\n        {\n            return true;\n        }\n    }\n\n    return false;\n}