using (SqlConnection cn = new SqlConnection(ConfigurationManager.ConnectionStrings["Northwind"].ConnectionString))\n{\n    string sql = "SELECT * FROM tbh_Categories WHERE CategoryID = @CategoryID";\n    SqlCommand cmd = new SqlCommand(sql, cn);\n    cmd.CommandType = CommandType.Text;\n    cmd.Parameters.Add("@CategoryID", SqlDbType.Int).Value = category.ID;\n\n    SqlDataAdapter da = new SqlDataAdapter(cmd);\n    DataSet ds = new DataSet();\n\n    da.Fill(ds);\n\n    // Update the data in the DataSet\n    ds.Tables[0].Rows[0]["Title"] = category.Title;\n    ds.Tables[0].Rows[0]["Description"] = category.Description;\n\n    // Update the database with the changes\n    SqlCommandBuilder cb = new SqlCommandBuilder(da);\n    da.Update(ds);\n}