using System;\nusing System.IO;\nusing System.Data.SqlClient;\n\npublic partial class UploadImage : System.Web.UI.Page\n{\n    protected void UploadButton_Click(object sender, EventArgs e)\n    {\n        if (FileUpload1.HasFile)\n        {\n            string connectionString = "Your SQL Server connection string";\n            using (SqlConnection connection = new SqlConnection(connectionString))\n            {\n                connection.Open();\n\n                string query = "INSERT INTO Images (ImageData, ContentType, FileName) VALUES (@ImageData, @ContentType, @FileName)";\n                SqlCommand command = new SqlCommand(query, connection);\n\n                command.Parameters.AddWithValue("@ImageData", FileUpload1.FileBytes);\n                command.Parameters.AddWithValue("@ContentType", FileUpload1.PostedFile.ContentType);\n                command.Parameters.AddWithValue("@FileName", FileUpload1.FileName);\n\n                command.ExecuteNonQuery();\n            }\n        }\n    }\n}