using System.IO;\nusing System.Reflection;\n\npublic class MyLibrary\n{\n    static MyLibrary()\n    {\n        // Get the embedded resource\n        string resourceName = "Skype4COM.dll";\n        Stream stream = Assembly.GetExecutingAssembly().GetManifestResourceStream(resourceName);\n\n        // Load the embedded DLL into memory\n        byte[] bytes = new byte[stream.Length];\n        stream.Read(bytes, 0, bytes.Length);\n        Assembly skypeAssembly = Assembly.Load(bytes);\n\n        // Register the Skype4COM.dll for COM interop\n        // This is equivalent to running regsvr32.exe\n        skypeAssembly.GetType("Skype4COM.SkypeClass").CreateInstance();\n    }\n\n    // ...\n}