using System.Web.Mvc;\nusing System.Web.UI;\n\npublic class RenderController : ApiController\n{\n    [HttpGet]\n    public string RenderControl(string controlName, string parameters)\n    {\n        // Create a new instance of the control\n        var control = (Control)Activator.CreateInstance(Type.GetType(controlName));\n\n        // Set the control's properties from the parameters\n        control.GetType().GetProperty("SomeProperty").SetValue(control, parameters);\n\n        // Render the control to a string\n        var writer = new StringWriter();\n        control.RenderControl(new HtmlTextWriter(writer));\n        return writer.ToString();\n    }\n}