using JetBrains.Profiler.Sdk;\n\nclass MyClass\n{\n    [Profiled]\n    public void MyMethod()\n    {\n        // Code to profile\n    }\n}\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        using (var profiler = new Profiler())\n        {\n            profiler.Start();\n            var myClass = new MyClass();\n            myClass.MyMethod();\n            profiler.Stop();\n            profiler.GetSnapshotAndShow();\n        }\n    }\n}