[XmlType]\npublic class BooleanType\n{\n    public static readonly BooleanType True = new BooleanType { Value = true };\n    public static readonly BooleanType False = new BooleanType { Value = false };\n\n    [XmlText]\n    public bool Value { get; set; }\n\n    public override string ToString()\n    {\n        return Value ? "1" : "0";\n    }\n}\n\n[XmlRoot("root")]\npublic class Root\n{\n    [XmlElement("emulate")]\n    public BooleanType Emulate { get; set; }\n}