using System.Data.OleDb;\n\n// Create a new OleDbConnection\nstring connectionString = "Provider=MSDAORA;Data Source=YourOracleServer;User ID=YourUsername;Password=YourPassword;";\nOleDbConnection connection = new OleDbConnection(connectionString);\n\n// Create a new OleDbCommand with the SQL query and parameters\nstring sqlQuery = "SELECT * FROM YourTable WHERE YourColumn = :YourParameter";\nOleDbCommand command = new OleDbCommand(sqlQuery, connection);\n\n// Manually define the parameter and its data type\nOleDbParameter parameter = new OleDbParameter("YourParameter", OleDbType.VarChar, 50);\ncommand.Parameters.Add(parameter);\n\n// Set the parameter value\nparameter.Value = "YourParameterValue";\n\n// Execute the query\nconnection.Open();\nOleDbDataReader reader = command.ExecuteReader();